--
-- Table structure for table `prelaunch_signup`
--
----- THIS TABLE IS NO LONGER POPULATED ----
----- HOWEVER, IT IS MAINTAINED AND NEW SIGNUPS W/ MATCHING EMAILS ARE DELETED ---
----- AT LAUNCH (AFTER PRE-LAUNCH ENDED) ANY REMAINING IN THIS TABLE SHOULD BE CONTACTED ---
CREATE TABLE IF NOT EXISTS `prelaunch_signup` (
  `emailAddress` varchar(64) DEFAULT NULL,
  `created` int(11) DEFAULT NULL,
  `ipaddress` varchar(45) DEFAULT NULL, /* jwg - allow for ipv6 */
  `referrerCreated` int(11) DEFAULT NULL, /* jwg - track unreg'd referrer by their prelaunch_signup created timestamp */
  `notified` tinyint(1) DEFAULT 0,
  PRIMARY KEY (`emailAddress`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1 ;
